import{_ as t,c as a,o as i,ag as o}from"./chunks/framework.bCALENAD.js";const p=JSON.parse('{"title":"Authority Kernel Threat Model","description":"","frontmatter":{},"headers":[],"relativePath":"design/ak-threat-model.md","filePath":"design/ak-threat-model.md","lastUpdated":1768808469000}'),r={name:"design/ak-threat-model.md"};function l(n,e,s,d,c,h){return i(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="authority-kernel-threat-model" tabindex="-1">Authority Kernel Threat Model <a class="header-anchor" href="#authority-kernel-threat-model" aria-label="Permalink to &quot;Authority Kernel Threat Model&quot;">​</a></h1><p><strong>Version:</strong> 1.0 <strong>Date:</strong> 2026-01-16 <strong>Status:</strong> ACTIVE</p><hr><h2 id="_1-overview" tabindex="-1">1. Overview <a class="header-anchor" href="#_1-overview" aria-label="Permalink to &quot;1. Overview&quot;">​</a></h2><p>This document defines the threat model for the Authority Kernel (AK) deny-by-default security system.</p><hr><h2 id="_2-attacker-model" tabindex="-1">2. Attacker Model <a class="header-anchor" href="#_2-attacker-model" aria-label="Permalink to &quot;2. Attacker Model&quot;">​</a></h2><h3 id="_2-1-threat-actors" tabindex="-1">2.1 Threat Actors <a class="header-anchor" href="#_2-1-threat-actors" aria-label="Permalink to &quot;2.1 Threat Actors&quot;">​</a></h3><p><strong>Primary: Malicious/Compromised Agent Code</strong></p><ul><li>AI agent code that attempts to exceed its authorized capabilities</li><li>Agents that have been prompt-injected or otherwise compromised</li><li>Tools or WASM modules with malicious intent</li></ul><p><strong>Secondary: Malicious External Input</strong></p><ul><li>Untrusted data from external APIs</li><li>User input designed to trigger unintended behavior</li><li>Network responses containing malicious payloads</li></ul><h3 id="_2-2-attacker-capabilities" tabindex="-1">2.2 Attacker Capabilities <a class="header-anchor" href="#_2-2-attacker-capabilities" aria-label="Permalink to &quot;2.2 Attacker Capabilities&quot;">​</a></h3><p>We assume the attacker can:</p><ul><li>Execute arbitrary code within the agent sandbox</li><li>Make arbitrary syscalls (which will be mediated by AK)</li><li>Craft malicious tool inputs/outputs</li><li>Attempt to confuse policy matching through encoding tricks</li><li>Attempt time-of-check to time-of-use (TOCTOU) attacks</li></ul><h3 id="_2-3-attacker-limitations" tabindex="-1">2.3 Attacker Limitations <a class="header-anchor" href="#_2-3-attacker-limitations" aria-label="Permalink to &quot;2.3 Attacker Limitations&quot;">​</a></h3><p>We assume the attacker CANNOT:</p><ul><li>Execute code in kernel space</li><li>Modify kernel memory directly</li><li>Bypass CPU protection mechanisms (rings, MMU)</li><li>Access hardware directly (all I/O is virtualized)</li><li>Modify the policy after it&#39;s loaded</li></ul><hr><h2 id="_3-security-goals" tabindex="-1">3. Security Goals <a class="header-anchor" href="#_3-security-goals" aria-label="Permalink to &quot;3. Security Goals&quot;">​</a></h2><h3 id="_3-1-non-bypass-inv-1" tabindex="-1">3.1 Non-Bypass (INV-1) <a class="header-anchor" href="#_3-1-non-bypass-inv-1" aria-label="Permalink to &quot;3.1 Non-Bypass (INV-1)&quot;">​</a></h3><p><strong>Goal:</strong> No effectful operation can bypass Authority Kernel mediation.</p><p><strong>Mechanism:</strong></p><ul><li>All syscalls route through <code>ak_authorize_and_execute()</code></li><li>POSIX syscalls are translated to AK effects</li><li>No direct hardware access from userspace</li><li>WASM hostcalls are capability-gated</li></ul><h3 id="_3-2-deny-by-default-inv-deny" tabindex="-1">3.2 Deny-by-Default (INV-DENY) <a class="header-anchor" href="#_3-2-deny-by-default-inv-deny" aria-label="Permalink to &quot;3.2 Deny-by-Default (INV-DENY)&quot;">​</a></h3><p><strong>Goal:</strong> Any operation not explicitly allowed by policy is denied.</p><p><strong>Mechanism:</strong></p><ul><li>Policy matching returns DENY if no rule matches</li><li>Missing policy = fail closed</li><li>Empty policy sections = deny that category</li></ul><h3 id="_3-3-capability-integrity-inv-2" tabindex="-1">3.3 Capability Integrity (INV-2) <a class="header-anchor" href="#_3-3-capability-integrity-inv-2" aria-label="Permalink to &quot;3.3 Capability Integrity (INV-2)&quot;">​</a></h3><p><strong>Goal:</strong> Capabilities cannot be forged or escalated.</p><p><strong>Mechanism:</strong></p><ul><li>Capabilities are HMAC-protected</li><li>Kernel controls capability issuance</li><li>Delegation can only attenuate, never escalate</li><li>Revocation is immediate and audited</li></ul><h3 id="_3-4-budget-enforcement-inv-3" tabindex="-1">3.4 Budget Enforcement (INV-3) <a class="header-anchor" href="#_3-4-budget-enforcement-inv-3" aria-label="Permalink to &quot;3.4 Budget Enforcement (INV-3)&quot;">​</a></h3><p><strong>Goal:</strong> Resource consumption is bounded and tracked.</p><p><strong>Mechanism:</strong></p><ul><li>Per-run budget limits (tokens, calls, time, bytes)</li><li>Pre-admission budget check</li><li>Exceeded budget = operation denied</li></ul><h3 id="_3-5-audit-integrity-inv-4" tabindex="-1">3.5 Audit Integrity (INV-4) <a class="header-anchor" href="#_3-5-audit-integrity-inv-4" aria-label="Permalink to &quot;3.5 Audit Integrity (INV-4)&quot;">​</a></h3><p><strong>Goal:</strong> All effectful operations are logged and logs are tamper-evident.</p><p><strong>Mechanism:</strong></p><ul><li>Hash-chained audit entries</li><li>Synchronous logging for control-plane</li><li>Bounded ring buffer for data-plane</li><li>Audit append before response</li></ul><hr><h2 id="_4-attack-vectors-and-mitigations" tabindex="-1">4. Attack Vectors and Mitigations <a class="header-anchor" href="#_4-attack-vectors-and-mitigations" aria-label="Permalink to &quot;4. Attack Vectors and Mitigations&quot;">​</a></h2><h3 id="_4-1-path-traversal" tabindex="-1">4.1 Path Traversal <a class="header-anchor" href="#_4-1-path-traversal" aria-label="Permalink to &quot;4.1 Path Traversal&quot;">​</a></h3><p><strong>Attack:</strong> Use <code>..</code> or symbolic links to access files outside allowed paths.</p><p><strong>Mitigation:</strong></p><ul><li>Canonicalization before policy matching</li><li>Normalize <code>.</code> and <code>..</code> segments</li><li>Lexical canonicalization (symlinks not resolved in P0)</li></ul><h3 id="_4-2-encoding-confusion" tabindex="-1">4.2 Encoding Confusion <a class="header-anchor" href="#_4-2-encoding-confusion" aria-label="Permalink to &quot;4.2 Encoding Confusion&quot;">​</a></h3><p><strong>Attack:</strong> Use different encodings to confuse policy matching (e.g., %2e%2e for ..)</p><p><strong>Mitigation:</strong></p><ul><li>Decode before canonicalization</li><li>Single canonical form for all targets</li><li>Bounded buffers prevent overflow</li></ul><h3 id="_4-3-toctou-time-of-check-to-time-of-use" tabindex="-1">4.3 TOCTOU (Time-of-Check to Time-of-Use) <a class="header-anchor" href="#_4-3-toctou-time-of-check-to-time-of-use" aria-label="Permalink to &quot;4.3 TOCTOU (Time-of-Check to Time-of-Use)&quot;">​</a></h3><p><strong>Attack:</strong> Change target between policy check and actual operation.</p><p><strong>Mitigation:</strong></p><ul><li>Canonicalize immediately on syscall entry</li><li>Use canonical target for both check and operation</li><li>No user-controlled strings in policy matching</li></ul><h3 id="_4-4-capability-leakage" tabindex="-1">4.4 Capability Leakage <a class="header-anchor" href="#_4-4-capability-leakage" aria-label="Permalink to &quot;4.4 Capability Leakage&quot;">​</a></h3><p><strong>Attack:</strong> Extract or forge capability tokens.</p><p><strong>Mitigation:</strong></p><ul><li>Capabilities are HMAC-protected</li><li>Keys are kernel-internal only</li><li>Capabilities are bound to run_id</li><li>Rate limiting prevents brute force</li></ul><h3 id="_4-5-resource-exhaustion" tabindex="-1">4.5 Resource Exhaustion <a class="header-anchor" href="#_4-5-resource-exhaustion" aria-label="Permalink to &quot;4.5 Resource Exhaustion&quot;">​</a></h3><p><strong>Attack:</strong> Exhaust system resources to cause denial of service.</p><p><strong>Mitigation:</strong></p><ul><li>Budget limits on all resource types</li><li>Bounded buffers throughout</li><li>Rate limiting on deny logging</li><li>Ring buffer for data-plane audit</li></ul><h3 id="_4-6-policy-bypass-via-tools" tabindex="-1">4.6 Policy Bypass via Tools <a class="header-anchor" href="#_4-6-policy-bypass-via-tools" aria-label="Permalink to &quot;4.6 Policy Bypass via Tools&quot;">​</a></h3><p><strong>Attack:</strong> Use tools or WASM to perform operations that bypass AK.</p><p><strong>Mitigation:</strong></p><ul><li>Tools cannot access FS/NET directly</li><li>Tool FS/NET operations route through AK</li><li>WASM hostcalls are capability-gated</li><li>No ambient authority in tool runtime</li></ul><h3 id="_4-7-dns-rebinding" tabindex="-1">4.7 DNS Rebinding <a class="header-anchor" href="#_4-7-dns-rebinding" aria-label="Permalink to &quot;4.7 DNS Rebinding&quot;">​</a></h3><p><strong>Attack:</strong> DNS response changes between resolve and connect.</p><p><strong>Mitigation:</strong></p><ul><li>Separate <code>net.dns</code> and <code>net.connect</code> capabilities</li><li>DNS resolution is an effect requiring authorization</li><li>Policy can require IP-based connect authorization</li></ul><hr><h2 id="_5-trust-boundaries" tabindex="-1">5. Trust Boundaries <a class="header-anchor" href="#_5-trust-boundaries" aria-label="Permalink to &quot;5. Trust Boundaries&quot;">​</a></h2><h3 id="_5-1-trusted-components" tabindex="-1">5.1 Trusted Components <a class="header-anchor" href="#_5-1-trusted-components" aria-label="Permalink to &quot;5.1 Trusted Components&quot;">​</a></h3><ul><li>Kernel code (including AK)</li><li>Policy file (validated at load time)</li><li>Capability keys (kernel-internal)</li><li>Audit log storage</li></ul><h3 id="_5-2-untrusted-components" tabindex="-1">5.2 Untrusted Components <a class="header-anchor" href="#_5-2-untrusted-components" aria-label="Permalink to &quot;5.2 Untrusted Components&quot;">​</a></h3><ul><li>Agent application code</li><li>Tool implementations</li><li>WASM modules</li><li>External network responses</li><li>User input</li></ul><h3 id="_5-3-boundary-enforcement" tabindex="-1">5.3 Boundary Enforcement <a class="header-anchor" href="#_5-3-boundary-enforcement" aria-label="Permalink to &quot;5.3 Boundary Enforcement&quot;">​</a></h3><p>Every crossing from untrusted to trusted requires:</p><ol><li>Effect creation with canonical target</li><li>Policy authorization via <code>ak_authorize_and_execute()</code></li><li>Audit logging</li><li>Bounded, validated parameters</li></ol><hr><h2 id="_6-assumptions" tabindex="-1">6. Assumptions <a class="header-anchor" href="#_6-assumptions" aria-label="Permalink to &quot;6. Assumptions&quot;">​</a></h2><h3 id="_6-1-hardware-hypervisor" tabindex="-1">6.1 Hardware/Hypervisor <a class="header-anchor" href="#_6-1-hardware-hypervisor" aria-label="Permalink to &quot;6.1 Hardware/Hypervisor&quot;">​</a></h3><ul><li>CPU protection mechanisms work correctly</li><li>MMU enforces memory protection</li><li>Hypervisor (if any) is trusted</li></ul><h3 id="_6-2-kernel" tabindex="-1">6.2 Kernel <a class="header-anchor" href="#_6-2-kernel" aria-label="Permalink to &quot;6.2 Kernel&quot;">​</a></h3><ul><li>Kernel code is not compromised</li><li>Kernel memory is protected from userspace</li></ul><h3 id="_6-3-policy" tabindex="-1">6.3 Policy <a class="header-anchor" href="#_6-3-policy" aria-label="Permalink to &quot;6.3 Policy&quot;">​</a></h3><ul><li>Policy is created by trusted administrator</li><li>Policy is not modified after load</li></ul><h3 id="_6-4-cryptography" tabindex="-1">6.4 Cryptography <a class="header-anchor" href="#_6-4-cryptography" aria-label="Permalink to &quot;6.4 Cryptography&quot;">​</a></h3><ul><li>SHA-256 is collision-resistant</li><li>HMAC-SHA256 is unforgeable</li></ul><hr><h2 id="_7-out-of-scope" tabindex="-1">7. Out of Scope <a class="header-anchor" href="#_7-out-of-scope" aria-label="Permalink to &quot;7. Out of Scope&quot;">​</a></h2><p>The following are explicitly NOT protected against:</p><h3 id="_7-1-side-channels" tabindex="-1">7.1 Side Channels <a class="header-anchor" href="#_7-1-side-channels" aria-label="Permalink to &quot;7.1 Side Channels&quot;">​</a></h3><ul><li>Timing attacks on policy matching</li><li>Cache-based side channels</li><li>Speculative execution attacks</li></ul><h3 id="_7-2-physical-attacks" tabindex="-1">7.2 Physical Attacks <a class="header-anchor" href="#_7-2-physical-attacks" aria-label="Permalink to &quot;7.2 Physical Attacks&quot;">​</a></h3><ul><li>Physical access to hardware</li><li>Cold boot attacks</li><li>Hardware implants</li></ul><h3 id="_7-3-policy-errors" tabindex="-1">7.3 Policy Errors <a class="header-anchor" href="#_7-3-policy-errors" aria-label="Permalink to &quot;7.3 Policy Errors&quot;">​</a></h3><ul><li>Overly permissive policy configuration</li><li>Missing rules that should be present</li><li>Incorrect pattern specifications</li></ul><h3 id="_7-4-kernel-vulnerabilities" tabindex="-1">7.4 Kernel Vulnerabilities <a class="header-anchor" href="#_7-4-kernel-vulnerabilities" aria-label="Permalink to &quot;7.4 Kernel Vulnerabilities&quot;">​</a></h3><ul><li>Bugs in kernel code itself</li><li>Memory corruption in trusted code</li></ul><hr><h2 id="_8-security-invariant-summary" tabindex="-1">8. Security Invariant Summary <a class="header-anchor" href="#_8-security-invariant-summary" aria-label="Permalink to &quot;8. Security Invariant Summary&quot;">​</a></h2><table tabindex="0"><thead><tr><th>ID</th><th>Invariant</th><th>Description</th></tr></thead><tbody><tr><td>INV-1</td><td>No-Bypass</td><td>All effects through mediation</td></tr><tr><td>INV-2</td><td>Capability</td><td>Every effectful op needs valid cap</td></tr><tr><td>INV-3</td><td>Budget</td><td>Resource limits enforced</td></tr><tr><td>INV-4</td><td>Log Commitment</td><td>All ops audited before response</td></tr><tr><td>INV-DENY</td><td>Deny-by-Default</td><td>No implicit permissions</td></tr><tr><td>INV-SINGLE</td><td>Single Gate</td><td>One authorization function</td></tr><tr><td>INV-CANONICAL</td><td>Canonicalization</td><td>Targets normalized before match</td></tr><tr><td>INV-BOUNDED</td><td>Bounded Buffers</td><td>All buffers have max sizes</td></tr></tbody></table><hr><h2 id="_9-version-history" tabindex="-1">9. Version History <a class="header-anchor" href="#_9-version-history" aria-label="Permalink to &quot;9. Version History&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Version</th><th>Date</th><th>Changes</th></tr></thead><tbody><tr><td>1.0</td><td>2026-01-16</td><td>Initial version</td></tr></tbody></table>',106)])])}const m=t(r,[["render",l]]);export{p as __pageData,m as default};
